package org.ip.sesion01;

public class Distancia {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 3;
		int y = 4;
		double distanciaEntrePuntos = Math.sqrt(Math.pow(x, 2) + Math.pow(y, 2));
		System.out.println("La distancia entre los puntos (" + x + "," + y + ") es "+ distanciaEntrePuntos);
	}

}
